        /** Function for Randomize items on an array
         * @param items (array)
         * @return array
         * */
        function getRandom(items) {
            var j, x, i;
            for (i = items.length - 1; i > 0; i--) {
                j = Math.floor(Math.random() * (i + 1));
                x = items[i];
                items[i] = items[j];
                items[j] = x;
            }
            return items;
        }

        function objectValues(obj) {
            var res = [];
            for (var i in obj) {
                if (obj.hasOwnProperty(i)) {
                    res.push(obj[i]);
                }
            }
            return res;
        }

